CREATE TABLE products(product_id INT PRIMARY KEY AUTO_INCREMENT, pname VARCHAR(150) NOT NULL, brand VARCHAR(50) NOT NULL, price DOUBLE (10,2) NOT NULL, color VARCHAR(30) NOT NULL, dimension VARCHAR(60) NOT NULL, description TEXT NOT NULL, rating FLOAT NOT NULL,category varchar(20) NOT NULL, image1 VARCHAR(50) NOT NULL,image2 VARCHAR(50) NOT NULL, image3 VARCHAR(50) NOT NULL);